mapper.select:
select m.menu_id, m.name, m.price, m.description, m.grade, m.keyword, m.image, m.good, m.click, m.hot, m.ice, m.category_id, m.cafe_id, m.sweet, m.bitter, m.sour, c.name as cafeName
from menu m, cafe c
where m.cafe_id = c.cafe_id and m.menu_id=#{menuId}

select menu_id, name, price, description, grade, keyword, image, good, click, hot, ice, category_id, cafe_id, sweet, bitter, sour, cafeName
from (select a.*, rownum rn
	  from (
		select m.menu_id, m.name, m.price, m.description, m.grade, m.keyword, m.image, m.good, m.click, m.hot, m.ice, m.category_id, m.cafe_id, m.sweet, m.bitter, m.sour, c.name as cafeName
		from menu m, cafe c
		where m.cafe_id = c.cafe_id
		) a
	) b
where #{endPageNum} >= rn and rn >= #{startPageNum}

select menu_id, name, price, description, grade, keyword, image, good, click, hot, ice, category_id, cafe_id, sweet, bitter, sour, cafeName
from (select a.*, rownum rn
	  from (
		select m.menu_id, m.name, m.price, m.description, m.grade, m.keyword, m.image, m.good, m.click, m.hot, m.ice, m.category_id, m.cafe_id, m.sweet, m.bitter, m.sour, c.name as cafeName, nvl(r.review, 0) as review
		from cafe c, menu m left outer join (select menu_id, count(review_id) as review from review group by menu_id) r on m.menu_id = r.menu_id
		where m.cafe_id = c.cafe_id
		order by ${condition} desc
		) a
	) b
where #{endPageNum} >= rn and rn >= #{startPageNum}

