				from menu m, cafe c
				where m.cafe_id = c.cafe_id and m.cafe_id=#{cafeId})
		where replace(name, ' ', '') like '%'||#{keyword}||'%' or replace(description, ' ', '') like '%'||#{keyword}||'%' or replace(keyword, ' ', '') like '%'||#{keyword}||'%'
		) a
	) b
where #{endPageNum} >= rn and rn >= #{startPageNum}

